import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import axios from "axios";
import Chat from "./Chat";

const socket = io("http://localhost:5000");

function App() {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");

  useEffect(() => {
    axios.get("http://localhost:5000/messages").then((response) => {
      setMessages(response.data);
    });

    socket.on("receiveMessage", (message) => {
      setMessages((prev) => [...prev, message]);
    });

    return () => socket.off("receiveMessage");
  }, []);

  const sendMessage = () => {
    if (text.trim()) {
      socket.emit("sendMessage", { sender: "User", text });
      setText("");
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "500px", margin: "auto", fontFamily: "Arial, sans-serif" }}>
      <h2 style={{ textAlign: "center", color: "#4CAF50" }}>Chat App</h2>
      <div
        style={{
          border: "2px solid #4CAF50",
          borderRadius: "8px",
          padding: "10px",
          height: "300px",
          overflowY: "auto",
          marginBottom: "10px",
          backgroundColor: "#f1f1f1",
        }}
      >
        {messages.map((msg, index) => (
          <p key={index} style={{ padding: "5px", borderBottom: "1px solid #ddd" }}>
            <strong style={{ color: "#2E8B57" }}>{msg.sender}:</strong> {msg.text}
          </p>
        ))}
      </div>
      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
        style={{
          width: "75%",
          padding: "8px",
          borderRadius: "5px",
          border: "1px solid #4CAF50",
          marginRight: "5px",
        }}
      />
      <button
        onClick={sendMessage}
        style={{
          padding: "8px 15px",
          backgroundColor: "#4CAF50",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Send
      </button>
    </div>
  );
}

export default App;
